import React, { Component } from 'react'
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import { getBestSeller, getMenWear, getWomenWear } from '../../../services';


import { connect } from 'react-redux';
import { addToCart, addToWishlist, addToCompare } from '../../../actions/index';
import Product from '../common/product';


class SpecialProducts extends Component {
    render() {
        const { bestSeller,menWear,womenWear, symbol, addToCart, addToWishlist, addToCompare, type } = this.props;
       // console.log(menWear);
        return (
            <div>
                <div className="title1 section-t-space">
                    <h4>exclusive products</h4>
                    <h2 className="title-inner1">special products</h2>
                </div>
                <section className="section-b-space p-t-0">
                    <div className="container">
                        <Tabs className="theme-tab" >
                            <TabList className="tabs tab-title" >
                                <Tab>Best Seller</Tab>
                                <Tab>Fresh Accessories</Tab>
                                <Tab>Hipster Style</Tab>
                            </TabList>

                            <TabPanel>
                                <div className="no-slider row" >
                                {
                                        bestSeller.map((product, index) => {
                                        return    <Product key={index} product={product} symbol={symbol}
                                                onAddToCartClicked={() => addToCart(product, 1)}
                                                onAddToWishlistClicked={() => addToWishlist(product)}
                                                onAddToCompareClicked={() => addToCompare(product)}
                                            />
                                        })
                                    }
                                </div>
                            </TabPanel>
                            <TabPanel>
                                <div className="no-slider row" >
                                {
                                        
                                        menWear.map((product, index) => {
                                        return   <Product key={index} product={product} symbol={symbol}
                                                onAddToCartClicked={() => addToCart(product, 1)}
                                                onAddToWishlistClicked={() => addToWishlist(product)}
                                                onAddToCompareClicked={() => addToCompare(product)}
                                            />
                                        })
                                    }
                                </div>
                            </TabPanel>
                            <TabPanel>
                                <div className="no-slider row" >
                                {
                                        womenWear.map((product, index) => {
                                        return    <Product key={index} product={product} symbol={symbol}
                                                onAddToCartClicked={() => addToCart(product, 1)}
                                                onAddToWishlistClicked={() => addToWishlist(product)}
                                                onAddToCompareClicked={() => addToCompare(product)}
                                            />
                                        })
                                    }
                                </div>
                            </TabPanel>
                        </Tabs>
                    </div>
                </section>
            </div>
        )
    }
}



const mapStateToProps = (state, props) => ({
    bestSeller: getBestSeller(state.data.products),
    menWear: getMenWear(state.data.products),
    womenWear: getWomenWear(state.data.products),
    symbol: state.data.symbol
});

export default connect(mapStateToProps, { addToCart, addToWishlist, addToCompare })(SpecialProducts)
