import * as types from '../constants/ActionTypes';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { dataService } from '../services/data.service';

export const addToCart = (product, qty) => (dispatch) => {
    toast.success("Item Added to Cart !!");
    dispatch(addtoCartData(product, qty));
};

export const addtoCartData = (product, qty) => ({
    type: types.ADD_TO_CART,
    product,
    qty
});

export const addToWishlist = (product) => (dispatch) => {
    toast.success("Item Added to Wishlist !!");
    console.log(product);
    dispatch(addToWishlistData(product));
};

export const addToWishlistData = (product) => ({
    type: types.ADD_TO_WISHLIST,
    product
});

export const addToCompare = (product) => (dispatch) => {
    toast.success("Item Added to Compare !!");
    dispatch(addToCompareData(product));
};

export const addToCompareData = (product) => ({
    type: types.ADD_TO_COMPARE,
    product
});

export const getAllProducts = () => (dispatch) => {
    dataService.getAllProductList().then(
            products => {
                dispatch(receiveProducts(products));
            },
            error => {
                toast.error("Something went wrong !!", "Fashion Store");
            }
        )
}

export const receiveProducts = (products) => ({
    type: types.RECEIVE_PRODUCTS,
    products
});


export const fetchSingleProduct = productId => ({
    type: types.FETCH_SINGLE_PRODUCT,
    productId
});

export const AddToCartAndRemoveWishlist = (product, qty) => (dispatch) => {
    toast.success("Item Added to Cart !!");
    dispatch(addToCart(product));
    dispatch(removeFromWishlist(product));
}

export const removeFromWishlist = productId => (dispatch) => {
    debugger;
    toast.success("Item Removed from Wishlist !!");
    dispatch({
        type: types.REMOVE_FROM_WISHLIST,
        product_Id: productId
    });
}

export const removeFromCart = productId => (dispatch) => {
    toast.success("Item Removed from Cart !!");
    dispatch({
        type: types.REMOVE_FROM_CART,
        product_Id: productId
    });
}

export const removeFromCompare = productId => (dispatch) => {
    toast.success("Item Removed from Compare !!");
    dispatch({
        type: types.REMOVE_FROM_COMPARE,
        product_Id: productId
    });
}

export const incrementQty = (product, qty) => (dispatch) => {
    toast.success("Item Added to Cart !!");
    dispatch(addtoCartData(product, qty));
}

export const decrementQty = (productId) => (dispatch) => {
    toast.success("Item Decrement Qty to Cart !!");
    dispatch({
        type: types.DECREMENT_QTY,
        productId
    });
}

// Filter
export const filterBrand = (brand) => ({
    type: types.FILTER_BRAND,
    brand
});
export const filterColor = (color) => ({
    type: types.FILTER_COLOR,
    color
});

export const filterPrice = (value) => ({
    type: types.FILTER_PRICE,
    value
});

export const filterSort = (sort_by) => ({
    type: types.SORT_BY,
    sort_by
});
