import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import Modal from 'react-responsive-modal';
import 'react-responsive-modal/styles.css';

export default class Product extends Component {
    constructor() {
        super();

        this.state = {
            open: false,
            stock: 'InStock',
            quantity: 1,
            image: ''
        };
    }

    onClickHandle(img) {
        this.setState({
            image: img
        });
    }

    onOpenModal = () => {
        this.setState({ open: true });
    }

    onCloseModal = () => {
        this.setState({ open: false });
    }

    minusQty = () => {
        if (this.state.quantity > 1) {
            this.setState({ stock: 'InStock', quantity: this.state.quantity - 1 });
        }
    }
    plusQty = () => {
        
        if (this.props.product.quantity_limit >= this.state.quantity) {
            this.setState({ stock: 'InStock', quantity: this.state.quantity + 1 });
            console.log(this.state.quantity);
        } else {
            this.setState({ stock: 'Out of Stock !' });
        }
    }

    changeQty = (e) => {
        console.log(e.target.value);
        this.setState({ quantity: parseInt(e.target.value) });
    }

    render() {
        const { product, symbol, onAddToCartClicked, onAddToWishlistClicked, onAddToCompareClicked } = this.props;

        let RatingStars = [];
        for (let i = 0; i <= 5; i++) {
            RatingStars.push(<i className="fa fa-star" key={i} ></i>);
        }

        return (
            <div className="product-box">
                <div className="img-wrapper">
                    <div className="lable-block">
                        {(product.isNew == true) ? <span className="lable3">new</span> : ''}
                        {(product.on_sale == true) ? <span className="lable4">on sale</span> : ''}
                    </div>
                    <div className="front">
                        <Link to={`${process.env.PUBLIC_URL}/left-sidebar/product/${product.id}`} >
                            <img
                                Src={`${product.images ?
                                    this.state.image ? this.state.image :
                                        product.images[0] ? product.images[0].src : '' :
                                    product.pictures[0]}`} className="img-fluid" alt="" />
                        </Link>
                    </div>
                    <div className="cart-info cart-wrap">
                        <button title="Add to cart" onClick={onAddToCartClicked} >
                            <i className="fa fa-shopping-cart" aria-hidden="true"></i>
                        </button>
                        <a href="javascript:void(0)" title="Add to Wishlist" onClick={onAddToWishlistClicked}>
                            <i className="fa fa-heart" aria-hidden="true"></i>
                        </a>
                        <a href="javascript:void(0)" data-toggle="modal"
                            data-target="#quick-view"
                            title="Quick View"
                            onClick={this.onOpenModal}
                        ><i className="fa fa-search" aria-hidden="true"></i></a>

                        <Link to={`${process.env.PUBLIC_URL}/compare`} onClick={onAddToCompareClicked} ></Link>
                        <i className="fa fa-refresh" aria-hidden="true"></i>
                    </div>

                    {
                        product.images ?
                            <ul className="product-thumb-list" >
                                {
                                    product.images.map((vari, i) => {
                                        return <li key={i}
                                            className={`grid-thumb-img ${(vari.src === this.state.image) ? 'active' : ''}`} >
                                            <a href="javascript:void(0)" title="ajeet" >
                                                <img alt="" src={`${vari.src}`} onClick={() => this.onClickHandle(vari.src)} />
                                            </a>
                                        </li>
                                    })
                                }
                            </ul>
                            : ''
                    }


                </div>
                <div className="product-detail">
                    <div>
                        <div className="rating">
                            {/* {RatingStars} */}
                        </div>
                        <Link to={`${process.env.PUBLIC_URL}/left-sidebar/product/${product.id}`} >
                            <h6>{product.name}</h6>
                        </Link>
                        <h4>{product.prices.currency_symbol} {product.prices.sale_price}
                            <del><span className="money">{product.prices.currency_symbol} {product.prices.regular_price}</span></del>
                        </h4>

                    </div>
                </div>
                <Modal open={this.state.open} onClose={this.onCloseModal} center >
                    <div className="modal-dialog modal-lg modal-dialog-centered" role="document" >
                        <div className="modal-content quick-view-modal">
                            <div className="modal-body">
                                <div className="row">
                                    <div className="col-lg-6 col-xs-12">
                                        <div className="quick-view-img">
                                        <img
                                            Src={`${product.images ?
                                                this.state.image ? this.state.image :
                                                    product.images[0] ? product.images[0].thumbnail : '' :
                                                product.pictures[0]}`} className="img-fluid" alt="" />
                                        </div>
                                    </div>
                                    <div className="col-lg-6 rtl-text">
                                        <div className="product-right">
                                            <h2> {product.name} </h2>
                                            <h3>{product.prices.currency_symbol} {product.prices.sale_price}</h3>
                                            <div className="border-product">
                                                <h6 className="product-title">product details</h6>
                                                <p> {product.short_description} </p>
                                            </div>
                                            <div className="product-description border-product">

                                                <h6 className="product-title">quantity</h6>
                                                <div className="qty-box">
                                                    <div className="input-group">
                                                        <span className="input-group-prepend">
                                                            <button type="button" className="btn quantity-left-minus"
                                                                onClick={this.minusQty} data-type="minus" data-field="">
                                                                <i className="fa fa-angle-left"></i>
                                                            </button>
                                                        </span>
                                                        <input type="text" name="quantity"
                                                            className="form-control input-number"
                                                            value={this.state.quantity} onChange={this.changeQty} />
                                                        <span className="input-group-prepend">
                                                            <button type="button" className="btn quantity-right-plus"
                                                                onClick={this.plusQty} data-type="plus" data-field="">
                                                                <i className="fa fa-angle-right"></i>
                                                            </button>
                                                        </span>
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="product-buttons">
                                                <button className="btn btn-solid" onClick={() => onAddToCartClicked(product,this.state.quantity)} >add to cart</button>
                                                <Link to={`${process.env.PUBLIC_URL}/left-sidebar/product/${product.id}`} className="btn btn-solid" >View Details</Link>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </Modal>
            </div>
        )
    }
}
