import { RECEIVE_PRODUCTS, FETCH_SINGLE_PRODUCT } from '../constants/ActionTypes';

const initialState = {
    products: [],
    symbol: '₹',
    product_details: []
};

const productReducer = (state = initialState, action) => {
   // debugger;
    switch (action.type) {
        case RECEIVE_PRODUCTS:
            return {
                ...state,
                products: action.products
            }

        case FETCH_SINGLE_PRODUCT:
            if (state.products.findIndex(product => product.id === action.productId) !== -1) {
                const singleItem = state.products.filter(product => product.id === action.productId)
                return {
                    ...state,
                    product_details: singleItem
                }
            }

        default:
            return state;
    }
};

export default productReducer;
