import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { dataService } from '../../../services/data.service';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Slider from 'react-slick'
import { Slider6 } from '../../../services/scripts';

export default class CollectionBanner extends Component {
    constructor() {
        super();
        this.state = { data: [] };
    }

    componentDidMount() {
        this.getCategoryData();
    }

    getCategoryData() {
        dataService.getCategoryWiseData()
            .then(
                res => {
                    //console.log(res);
                    this.setState({ data: res });
                    // if (res.isSuccess) {
                    //     this.setState({ data: res.data });
                    // } else {
                    //     // toast.error(res.errors[0], "Fashion Store");
                    // }
                },
                error => {
                    toast.error("Something went wrong !!", "Fashion Store");
                }
            )
    }
    render() {
        const { data } = this.state;
        return (
            <div>
                <section className="section-b-space">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-12">
                                <Slider {...Slider6} className="product-6 no arrow" >
                                    {
                                        data.map((data, index) => {
                                            //console.log(data.image.thumbnail);
                                            return (
                                                <div className="" >
                                                    <Link to={`product-category/${data.slug}`} key={data.id} >    
                                                        <img   className="rounded-circle categorySlider" alt="50x50" src={`${data.image ? data.image.thumbnail : `${process.env.PUBLIC_URL}/assets/images/logos/8.png`}`} alt={data.name} data-holder-rendered="true"></img>
                                                        <p className="categoryName" >{data.name}</p>
                                                    </Link>
                                                </div>
                                                
                                            )
                                        })
                                    }

                                </Slider>
                            </div>
                        </div>
                    </div>
                    <ToastContainer />
                </section>

                {/* <section className="pb-0">
                    <div className="container">
                        <div className="row partition2">
                            {
                                data.map((data, index) => {
                                    return (
                                        <div className="col-md-6" key={index}>
                                            <Link to={data.link} >
                                                <div className="collection-banner p-right text-center">
                                                    <img src={data.imagePath} className="img-fluid" alt={data.title} />
                                                    <div className="contain-banner">
                                                        <div>
                                                            <h4>save {data.isSave}%</h4>
                                                            <small>{data.name}</small>
                                                        </div>
                                                    </div>
                                                </div>
                                            </Link>
                                        </div>
                                    )
                                })
                            }
                        </div>
                    </div>
                   
                </section> */}
            </div>
        )
    }
}
