import React, { Component } from 'react'
import Slider from 'react-slick'
import { Product4 } from '../../../services/scripts';

import { connect } from 'react-redux';
import { getSpecialCollection } from '../../../services/index';
import { addToCart, addToWishlist, addToCompare } from '../../../actions/index';
import Product from '../common/product';

class TopCollection extends Component {
    render() {
        const { items, symbol, addToCart, addToWishlist, addToCompare, type } = this.props;
        return (
            <div>
                {/*Paragraph*/}
                <div className="title1 section-t-space">
                    <h4>special offer</h4>
                    <h2 className="title-inner1">top collection</h2>
                </div>
                {/*Paragraph End*/}
                <section className="section-b-space p-t-0">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <Slider {...Product4} className="product-4 product-m no arrow" >
                                    {
                                        items.map((product, index) => {
                                            
                                          return  <Product key={index} product={product} symbol={symbol}
                                                onAddToCartClicked={() => addToCart(product, 1)}
                                                onAddToWishlistClicked={() => addToWishlist(product)}
                                                onAddToCompareClicked={() => addToCompare(product)}
                                            />
                                        })
                                    }
                                </Slider>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}


const mapStateToProps = (state, props) => ({
    items: getSpecialCollection(state.data.products, props.type),
    symbol: state.data.symbol
});

export default connect(mapStateToProps, { addToCart, addToWishlist, addToCompare })(TopCollection)
